mapper.select:
SELECT frequently_asked_question_id, question, answer FROM m_frequently_asked_question WHERE delete_flg = #{deleteFlg}
AND (question LIKE '%${keyword}%' OR answer LIKE '%${keyword}%')
AND frequently_asked_question_category_id = #{frequentlyAskedQuestionCategoryId}

SELECT COUNT(*) 
FROM m_frequently_asked_question
WHERE frequently_asked_question_category_id = #{categoryId} 
	AND delete_flg = #{deleteFlg}

mapper.update:
UPDATE m_frequently_asked_question
SET
	delete_flg = #{deleteFlg},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE frequently_asked_question_id = #{frequentlyAskedQuestionId}

mapper.select:
SELECT * 
FROM m_frequently_asked_question
WHERE frequently_asked_question_id = #{faqId} 
	AND delete_flg = #{deleteFlg}
