mapper.select:
SELECT * 
FROM m_genre_detail
WHERE genre_detail_id = #{genreDetailId} 
AND delete_flg = #{deleteFlg}

mapper.update:
UPDATE m_genre_detail
SET
	delete_flg = #{deleteFlg},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE genre_id = #{genreId}

UPDATE m_genre_detail
SET
	delete_flg = #{deleteFlg},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE genre_detail_id = #{genreDetailId}

UPDATE m_genre_detail
SET
	genre_detail_name = #{genreDetailName},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE genre_detail_id = #{genreDetailId} 
	AND delete_flg = #{deleteFlg}

mapper.select:
SELECT
	genre_detail_id,
	genre_detail_name
FROM m_genre_detail
WHERE genre_id = #{genreId} 
	AND delete_flg = #{deleteFlg}
