mapper.select:
SELECT t1.genre_id, t1.genre_name, t2.genre_detail_id, t2.genre_detail_name FROM m_genre t1 LEFT OUTER JOIN m_genre_detail t2 ON t2.genre_id = t1.genre_id AND t2.delete_flg = #{deleteFlg} WHERE t1.delete_flg = #{deleteFlg}
AND t1.genre_name LIKE '%${genreName}%'
ORDER BY genre_id ASC

mapper.update:
UPDATE m_genre
SET
	delete_flg = #{deleteFlg},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE genre_id = #{genreId}

mapper.select:
SELECT * 
FROM m_genre
WHERE genre_id = #{genreId} 
	AND delete_flg = #{deleteFlg}

mapper.update:
UPDATE m_genre
SET
	genre_name = #{genreName},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE genre_id = #{genreId} 
	AND delete_flg = #{deleteFlg}
