mapper.select:
SELECT t1.movie_category_id, t1.movie_category_name, t2.movie_name, t2.url, t2.movie_id FROM m_movie_category t1 LEFT OUTER JOIN m_movie t2 ON t2.movie_category_id = t1.movie_category_id AND t2.delete_flg = #{deleteFlg} WHERE t1.delete_flg = #{deleteFlg}
AND t1.movie_category_name LIKE '%${movieCategoryName}%'
ORDER BY t1.movie_category_id ASC, t2.sort_number ASC

SELECT * 
FROM m_movie_category
WHERE movie_category_id = #{movieCategoryId} 
	AND delete_flg = #{deleteFlg}

mapper.update:
UPDATE m_movie_category
SET
	delete_flg = #{deleteFlg},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE movie_category_id = #{movieCategoryId}

UPDATE m_movie_category
SET
	movie_category_name = #{movieCategoryName},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE movie_category_id = #{movieCategoryId}
