mapper.select:
SELECT t1.section_id, t1.section_name, t1.section_description, t1.course_id,
t1.category_id, t1.date, t3.file_id, t3.file_name, t3.file_path, t4.exam_section_id, t5.exam_id, t5.exam_name, t5.genre_id, t4.public_date, CASE WHEN t4.public_date < CURRENT_TIMESTAMP THEN 'true' ELSE 'false' END AS public_flg,
CASE WHEN t11.exam_count IS NULL THEN false ELSE true END AS exam_flg, t7.daily_report_id, t8.daily_report_submit_id, t7.report_name, t9.deliverables_section_id, t9.submission_deadline, t10.deliverables_id, t10.deliverables_name FROM m_section t1 LEFT OUTER JOIN t_file_section t2 ON t2.section_id = t1.section_id LEFT OUTER JOIN m_file t3 ON t3.file_id = t2.file_id LEFT OUTER JOIN t_exam_section t4 ON t1.section_id = t4.section_id AND T4.delete_flg = #{deleteFlg} LEFT OUTER JOIN m_exam t5 ON t4.exam_id = t5.exam_id AND t5.delete_flg = #{deleteFlg} LEFT OUTER JOIN t_course_daily_report t6 ON t1.course_id = t6.course_id LEFT OUTER JOIN m_daily_report t7 ON t6.daily_report_id = t7.daily_report_id LEFT OUTER JOIN t_daily_report_submit t8 ON t8.date = t1.date AND t8.lms_user_id = #{lmsUserId} AND t7.daily_report_id = t8.daily_report_id AND t8.delete_flg = #{deleteFlg} LEFT OUTER JOIN t_deliverables_section t9 ON t1.section_id = t9.section_id AND t9.delete_flg = #{deleteFlg} LEFT OUTER JOIN m_deliverables t10 ON t9.deliverables_id = t10.deliverables_id AND t10.delete_flg = #{deleteFlg}
LEFT OUTER JOIN (SELECT exam_section_id, count(*) exam_count FROM t_exam_result WHERE lms_user_id = #{lmsUserId} GROUP BY exam_section_id) t11 ON t11.exam_section_id = t4.exam_section_id WHERE t1.section_id = #{sectionId} AND t1.account_id = #{accountId} AND t1.delete_flg = #{deleteFlg}
ORDER BY t4.public_date ASC

SELECT count(*) FROM m_section
WHERE section_id = #{sectionId}
	AND delete_flg = 0

SELECT
	count(*) FROM m_section
WHERE course_id = #{courseId} 
	AND date = #{date}
