mapper.select:
SELECT COUNT(*) 
FROM t_exam_result
WHERE exam_section_id = #{examSectionId} 
	AND lms_user_id = #{lmsUserId} 
	AND delete_flg = #{deleteFlg}

mapper.update:
UPDATE t_exam_result
SET
	delete_flg = #{deleteFlg},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE exam_section_id = #{examSectionId}
	AND lms_user_id = #{lmsUserId}
