mapper.update:
UPDATE t_meeting_company
SET
	meeting_schedule_detail_id = #{meetingScheduleDetailId},
	join_amount = #{joinAmount},
	company_request = #{companyRequest},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE meeting_place_id = #{meetingPlaceId}
	AND company_id = #{companyId}

mapper.select:
SELECT COUNT(*) 
FROM t_meeting_company
WHERE meeting_place_id = #{meetingPlaceId} 
	AND delete_flg = #{deleteFlg}
