mapper.select:
SELECT COUNT(*) 
FROM t_meeting_place
WHERE meeting_schedule_id = #{meetingScheduleId} 
	AND delete_flg = #{deleteFlg}

mapper.update:
UPDATE t_meeting_place
SET
	published_flg = #{publishedFlg},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE meeting_place_id = #{meetingPlaceId}

mapper.select:
SELECT meeting_place_id
FROM t_meeting_place
WHERE meeting_schedule_id = #{meetingScheduleId} 
	AND delete_flg = #{deleteFlg}
