mapper.update:
UPDATE t_presentation_company
SET
	join_able_flg = #{joinAbleFlg},
	join_amount = #{joinAmount},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate},
	join_name = #{joinName}
WHERE presentation_company_id = #{presentationCompanyId}
	AND delete_flg = #{deleteFlg}
