mapper.update:
UPDATE t_repair_history
SET
	delete_flg = #{deleteFlg},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE history_id = #{historyId}

mapper.select:
SELECT * 
FROM t_repair_history
WHERE history_id = #{historyId} 
AND delete_flg = #{deleteFlg}

mapper.update:
UPDATE t_repair_history
SET
	content = #{content},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE history_id = #{historyId}
