mapper.select:
SELECT place_id
FROM t_user_place
WHERE lms_user_id = #{lmsUserId} 
AND delete_flg = #{deleteFlg}

mapper.update:
UPDATE t_user_place
SET
	delete_flg = #{deleteFlg},
	last_modified_user = #{lastModifiedUser},
	last_modified_date = #{lastModifiedDate}
WHERE lms_user_id = #{lmsUserId}

mapper.select:
SELECT COUNT(*) 
FROM t_user_place
WHERE place_id = #{placeId} 
	AND delete_flg = #{deleteFlg}
